var strOper={add:function(r,t){for(var n=r.split("|"),i=!0,e=0;e<n.length;e++){var p=n[e].split("#");if(p[0]==t.split("#")[0]){i=!1,p[4]=parseInt(p[4])+parseInt(t.split("#")[4]),n[e]=p.join("#");break}}return i&&n.push(t),n.join("|")},get:function(r){for(var t=[],n=""==r?[]:r.split("|"),i=0;i<n.length;i++){var e=n[i].split("#"),p={id:e[0],name:e[1],img:e[2],price:e[3],count:e[4]};t.push(p)}return t},del:function(r,t){for(var n=[],i=r.split("|"),e=0;e<i.length;e++){var p=i[e].split("#");p[0]!=t&&n.push(p.join("#"))}return n.join("|")},counter:function(r,t,n){for(var i=r.split("|"),e=0;e<i.length;e++){var p=i[e].split("#");if(p[0]==t){n===!0?p[4]=parseInt(p[4])+1:n===!1&&(p[4]=parseInt(p[4])-1),i[e]=p.join("#");break}}return i.join("|")}};